.preloader {
    position: fixed;
    z-index: 9999;
    background: #ffffff;
    width: 100%;
    height: 100%;
    /* transition: all 1.3s ease-in-out;
    transform: translateY(-100%); */
    transition: all 1.3s 0.5s cubic-bezier(1, 0.03, 0.85, 1.07);
}

.loader {
    border: 0 solid transparent;
    border-radius: 50%;
    width: 200px;
    height: 200px;
    position: absolute;
    top: calc(50vh - 125px);
    left: calc(50vw - 125px)
}

.loader:before,
.loader:after {
    content: '';
    border: 1em solid #000000;
    border-radius: 50%;
    width: inherit;
    height: inherit;
    position: absolute;
    top: 0;
    left: 0;
    animation: load 2s linear infinite;
    opacity: 0;
}

.preload-finish {
    /* opacity: 0; */
    transform: translateY(-100%);
    pointer-events: none;
}

.loader:before {
    animation-delay: 5s;
}

@keyframes load {
    0% {
        transform: scale(0);
        opacity: 0;
    }
    50% {
        opacity: 1;
    }
    100% {
        transform: scale(1);
        opacity: 0;
    }
}

.ctrimg {
    border-radius: 50%;
    width: 60px;
    height: 60px;
    position: absolute;
    vertical-align: center;
    top: calc(50vh - 40px);
    left: calc(50vw - 40px);
    filter: invert(100%);
}

