    z-index: 900;
    font-family: Rockwell;
    font-style: normal;
    font-weight: normal;
    text-transform: uppercase;
}

.vdv {
    top: 2% !important;
    bottom: none !important;
    position: absolute;
    left: 25%;
    width: 50%;
}

.fp-tableCell {
    vertical-align: bottom;
}

.heading_txt {
    position: absolute;
}

.heading {
    -webkit-backdrop-filter: saturate(180%) blur(20px) brightness(50%) !important;
    -ms-backdrop-filter: saturate(180%) blur(20px) brightness(50%) !important;
    backdrop-filter: saturate(180%) blur(20px) brightness(50%) !important;
    background-color: rgba(0, 0, 0, 0.5);
    box-shadow: 35px 29px 29px rgba(0, 0, 0, 0.55);
    overflow: hidden;
    width: 85%;
    height: 30%;
    margin-left: auto;
    margin-right: auto;
}

.heading_txt {
    overflow: hidden;
    white-space: nowrap;
    font-family: Rockwell;
    font-style: normal;
    font-weight: normal;
    font-size: 12vw;
    color: rgb(255, 255, 255);
    letter-spacing: -0.27px;
    top: 6%;
    z-index: 605;
    padding-left: 4%;
    padding-right: 4%;
    padding-top: 3%;
    padding-bottom: 3%;
}

.VDV {
    display: none;
}

.overlay {
    display: none;
}

